    "index.html": "<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Todo App</title>
  <link href="https://cdn.jsdelivr.net/npm/tailwindcss@2.2.19/dist/tailwind.min.css" rel="stylesheet">
  <link rel="stylesheet" href="src/styles.css">
</head>
<body class="bg-cover bg-no-repeat bg-fixed h-screen w-screen" style="background-image: url('https://plus.unsplash.com/premium_photo-1673306778968-5aab577a7365?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D');">
  <div class="container mx-auto p-4 bg-white rounded-lg shadow-md">
    <h1 class="text-3xl font-bold mb-4 text-center">Todo List</h1>
    <div class="flex mb-4">
      <input type="text" id="todo-input" class="w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500" placeholder="Add a todo">
      <button id="add-button" class="ml-2 px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-700">Add</button>
    </div>
    <ul id="todo-list" class="list-none"></ul>
  </div>
  <script src="src/main.js"></script>
</body>
</html>",
    "src/styles.css": "",
    "src/main.js": "const todoInput = document.getElementById('todo-input');
const addButton = document.getElementById('add-button');
const todoList = document.getElementById('todo-list');

function loadTodos() {
  const storedTodos = localStorage.getItem('todos');
  if (storedTodos) {
    const todos = JSON.parse(storedTodos);
    todos.forEach(addTodoItem);
  }
}

function saveTodos() {
  const todos = Array.from(todoList.children).map(item => item.querySelector('.todo-text').textContent);
  localStorage.setItem('todos', JSON.stringify(todos));
}

addButton.addEventListener('click', () => {
  const todoText = todoInput.value.trim();
  if (todoText !== '') {
    addTodoItem(todoText);
    todoInput.value = '';
    saveTodos();
  }
});

function addTodoItem(text) {
  const newTodoItem = document.createElement('li');
  newTodoItem.innerHTML = `<div class="flex items-center py-2">
                              <input type="checkbox" class="mr-2">
                              <span class="todo-text" contenteditable="true">${text}</span>
                              <button class="ml-auto px-2 py-1 bg-yellow-500 text-white rounded-md hover:bg-yellow-700 edit-button">Edit</button>
                              <button class="ml-2 px-2 py-1 bg-red-500 text-white rounded-md hover:bg-red-700 delete-button">Delete</button>
                            </div>`;
  todoList.appendChild(newTodoItem);
}

todoList.addEventListener('click', (event) => {
  if (event.target.classList.contains('delete-button')) {
    event.target.parentElement.parentElement.remove();
    saveTodos();
  } else if (event.target.classList.contains('edit-button')) {
    //No action needed. ContentEditable handles editing.
  }
});

// Load todos on page load
loadTodos();"
  }
}
