{
  "name": "Physics-Based Game",
  "description": "A physics-based game using HTML, CSS, JavaScript, and Fabric.js with Tailwind CSS for styling.",
  "technologies": ["HTML", "CSS", "JavaScript", "Fabric.js", "Tailwind CSS"],
  "installation": [
    "npm install -D tailwindcss",
    "npx tailwindcss init -p",
    "npm install fabric"
  ],
  "files": [
    "index.html",
    "styles.css",
    "script.js"
  ],
  "code": {
    "index.html": "<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Physics Game</title>
  <link href="/dist/output.css" rel="stylesheet">
  <script src="https://cdnjs.cloudflare.com/ajax/libs/fabric.js/5.2.4/fabric.min.js"></script>
</head>
<body class="bg-gray-100">
  <canvas id="canvas"></canvas>
  <script src="script.js"></script>
</body>
</html>",
    "styles.css": "@tailwind base;
@tailwind components;
@tailwind utilities;",
    "script.js": "const canvas = new fabric.Canvas('canvas');
canvas.setWidth(window.innerWidth);
canvas.setHeight(window.innerHeight);

const rect = new fabric.Rect({
  left: 100,
  top: 100,
  fill: 'red',
  width: 50,
  height: 50
});

canvas.add(rect);

canvas.on('object:moving', function(e) {
  const obj = e.target;
  obj.set({left: obj.left, top: obj.top});
  canvas.renderAll();
});"
  }
}
