{
  "name": "Pendulum Experiment",
  "description": "A simulation of a simple pendulum using HTML, CSS, JavaScript, and Fabric.js, styled with Tailwind CSS.",
  "technologies": ["HTML", "CSS", "JavaScript", "Fabric.js", "Tailwind CSS"],
  "installation": [
    "npm install -D tailwindcss",
    "npx tailwindcss init -p",
    "npm install fabric"
  ],
  "files": [
    "index.html",
    "styles.css",
    "script.js"
  ],
  "code": {
    "index.html": "<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Pendulum Experiment</title>
  <link href="/dist/output.css" rel="stylesheet">
  <script src="https://cdnjs.cloudflare.com/ajax/libs/fabric.js/5.2.4/fabric.min.js"></script>
</head>
<body class="bg-gray-100">
  <canvas id="canvas"></canvas>
  <script src="script.js"></script>
</body>
</html>",
    "styles.css": "@tailwind base;
@tailwind components;
@tailwind utilities;",
    "script.js": "const canvas = new fabric.Canvas('canvas');
canvas.setWidth(window.innerWidth);
canvas.setHeight(window.innerHeight);

const length = 200;
const bobRadius = 20;

const bob = new fabric.Circle({
  radius: bobRadius,
  fill: 'red',
  left: 100,
  top: 100,
  originX: 'center',
  originY: 'center'
});

const line = new fabric.Line([100, 100, 100, 100 + length], {
  stroke: 'black',
  strokeWidth: 2
});

canvas.add(line);
canvas.add(bob);

let angle = 0;
let angularVelocity = 0;
let angularAcceleration = 0;

function animate() {
  angularAcceleration = -9.81 / length * Math.sin(angle);
  angularVelocity += angularAcceleration;
  angle += angularVelocity;

  bob.left = 100 + length * Math.sin(angle);
  bob.top = 100 + length * (1 - Math.cos(angle));
  line.set({x2: bob.left, y2: bob.top});

  canvas.renderAll();
  requestAnimationFrame(animate);
}

animate();"
  }
}
