const numInvaders = 20; // Increased number of invaders
const invaderTypes = [
  { width: 15, height: 10, color: "red", points: 10 },
  { width: 20, height: 15, color: "blue", points: 20 },
  { width: 25, height: 20, color: "purple", points: 30 }
];
